import React, { useCallback } from "react";
import axios from "axios";
import { useDispatch, useSelector, useStore } from "react-redux"; //dùng useStore khi cần lấy dữ liệu trên store xuống dùng mà component ko cần phải render lại, còn nếu component phải render lại thì dùng useSelector (do useSeclector đổi thì state của component cũng đổi nên phải render lại)

const Control = () => {
  //Hàm useStore dùng để lấy dữ liệu về dùng mà ko thay đổi state của component như useSelector
  const store = useStore();
  // store.getState()

  const deckCard = useSelector((state) => {
    return state.card.deckCard;
  });

  const playList = useSelector((state) => {
    return state.player.playerList;
  });
  const dispatch = useDispatch();

  const {isReveal, isDraw, isShuffle} = store.getState();
  const {card} = store.getState();
  
  console.log(store.getState());

  const drawCards = useCallback(() => {
    axios({
      method: "GET",
      url: `http://deckofcardsapi.com/api/deck/new/draw/?count=12`
    })
      .then((res) => {
        const players = [...playList];
        //chia bài
        for (const i in res.data.cards) {
          const playerIndex = i % 4;
          players[playerIndex].cards.push(res.data.cards[i]);

          //i = 0, playerIndex = 0 => card[0] => player[0]
          //i = 1, playerIndex = 1 => card[1] => player[1]
          //i = 2, playerIndex = 2 => card[2] => player[2]
          //i = 3, playerIndex = 3 => card[3] => player[3]
          //i = 4, playerIndex = 0 => card[4] => player[0]
          //i = 5, playerIndex = 1 => card[5] => player[1]
          //i = 6, playerIndex = 2 => card[6] => player[2]
        }
        dispatch({ type: "SET_PLAYERS", payload: players });
      })
      .catch((err) => {
        console.log(err);
      });
  }, [deckCard, playList]);

  

  const setPoint = (players, winnerList, gain, lose) => {
    return players.map((player) => {
      let newItem = { ...player };

      let winner = winnerList.find((item) => player.username === item.username);

      if (winner) {
        newItem.totalPoint = player.totalPoint + gain;
      } else {
        newItem.totalPoint = player.totalPoint - lose;
      }
      return newItem;
    });
  };

  const revealCards = useCallback(() => {
    //1. Lật bài
    dispatch({ type: "REVEAL_CARDS" });
    //2. tính kết quả
    //check playerList

    //TH còn lại: cộng điểm người chơi, tìm max
    //note: có thể có nhiều hơn 1 người thắng, danh sách người thắng phải là 1 cái mảng
    let players = [...playList];
    let winnerList = [{ username: "", result: 0, totalPoint: 25000 }];

    const resultList = players.map((item) => {
      //TH đặc biệt : King, Jack, Queen
      let arrKJQCase = item.cards.filter(
        (card) =>
          card.value === "QUEEN" ||
          card.value === "KING" ||
          card.value === "JACK"
      );
      if (arrKJQCase.length === 3) {
        winnerList.push(item);
      } else {
        const point = item.cards.reduce((pointCard, card, index) => {
          if (card.value === "ACE") {
            card.value = 1;
          }
          if (
            card.value === "QUEEN" ||
            card.value === "KING" ||
            card.value === "JACK"
          ) {
            card.value = 10;
          }

          pointCard += +card.value;
          return pointCard;
        }, 0);

        let playerList = {
          username: item.username,
          result: point % 10,
          totalPoint: item.totalPoint
        };
        return playerList;
      }
    });

    for (let player of resultList) {
      if (player.result > winnerList[0].result) {
        winnerList = [{ ...player }];
      }
      if (player.result === winnerList[0].result) {
        if (!winnerList.find((item) => item.username === player.username)) {
          winnerList.push(player);
        }
      }
    }
    switch (winnerList.length) {
      case 1:
        players = setPoint(players, winnerList, 3000, 1000);
        break;
      case 2:
        players = setPoint(players, winnerList, 1000, 1000);
        break;
      case 3:
        players = setPoint(players, winnerList, Math.round(1000 / 3), 1000);
        break;
      default:
        players = setPoint(players, winnerList, 0, 0);
        break;
    }
    //3.Dispatch new playerList với new totalPoint
    dispatch({ type: "SET_PLAYERS", payload: players });
  }, [dispatch, setPoint]);

  console.log(isShuffle);
  console.log(isDraw);
  return (
    <div className="d-flex  justify-content-end container">
      <div className="border d-flex justify-content-center align-items-center px-2">
        <button className="btn btn-success mr-2" disabled={!isShuffle}>Shuffle</button>
        <button className="btn btn-info mr-2" onClick={drawCards} disabled={!isDraw}>
          Draw
        </button>
        <button className="btn btn-primary mr-2" onClick={revealCards} disabled={!isReveal}>
          Reveal
        </button>
      </div>
      <div className="d-flex">
        {playList.map((item) => {
          return (
            <div key={item.username} className="border px-3 text-center">
              <p>{item.username}</p>
              <p> {item.totalPoint} </p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Control;
