import React, { useState } from "react";
import Game from "../Game";
import { useFormik } from "formik";
import * as yup from "yup";
import { useDispatch } from "react-redux";

// React hook là những function cho ta sử dụng các tính năng của state, lifecycle =>PHẢI ĐƯỢC ĐẶT TRÊN CÙNG VÀ KO LỒNG TRONG BẤT CỨ HÀM NÀO KHÁC
const validationSchema = yup.object().shape({
  username: yup.string().required("This field is requied!"),
  email: yup.string().required("This field is requied").email("Invalid email!"),
  phone: yup
    .string()
    .required("This field is requied")
    .matches(/^[0-9]+$/, "Invalid phone number!"),
});

const Home = () => {
  const [isGameStart, setIsGameStart] = useState(false);
  // const [a, setA] = useState();
  // const [b, setB] = useState();
  const formik = useFormik({
    initialValues: {
      username: "",
      email: "",
      phone: "",
    },
    validationSchema: validationSchema, //có thể viết validationSchema không do biến trùng với key
    validateOnMount: true, // cho chạy hàm check validationSchema luôn, nên cho chạy luôn vì nếu ko bật thì mới vào ko nhập nó sẽ ko bắt lỗi mà click btn thì chạy handleSubmit luôn
  });
  const setAllTouched = () => {
    Object.keys(formik.values).forEach((key) => {
      formik.setFieldTouched(key);
      console.log(Object.keys(formik.values));
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setAllTouched();
    console.log(formik.touched);
    if (!formik.isValid) return; //check nếu form ko có lỗi (ko có false trong bất kỳ trường nào), thì mới cho vào game
    setIsGameStart(true);
  };
  const setDefaultPlayer = () => {
    const defaultPlayer= {
      username: "Thao.Le",
      email: "Thao@gmail.com",
      phone: "123",
    }
    formik.setValues(defaultPlayer)
  }
  return (
    <>
      {isGameStart ? (
        <Game />
      ) : (
        <div
          className="text-center"
          style={{
            width: "100vw",
            height: "100vh",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <h1 className="diplay-4 mb-5"> Welcome to Pocker Center</h1>
          <h3>Fill your info and start</h3>
          <form onSubmit={handleSubmit} className="w-25 mx-auto">
            <input
              type="input"
              name="username"
              value={formik.values.username}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              placeholder="username"
              className="w-100 form-control mb-3"
            />
            {formik.touched.username && (
              <p className="text-danger">{formik.errors.username}</p>
            )}
            <input
              type="input"
              name="email"
              value={formik.values.email}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              placeholder="email"
              className="w-100 form-control mb-3"
            />
            {formik.touched.email && (
              <p className="text-danger">{formik.errors.username}</p>
            )}

            <input
              type="input"
              name="phone"
              value={formik.values.phone}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              placeholder="phone"
              className="w-100 form-control mb-3"
            />
            {formik.touched.phone && (
              <p className="text-danger">{formik.errors.phone}</p>
            )}

            <button className="btn btn-success">Start new Game</button>
            <button type="button" onClick={setDefaultPlayer} className="btn btn-info">Set dafault player</button>
          </form>
        </div>
      )}
    </>
  );
};

export default Home;
