import React, { useState } from "react";
import Game from "../Game";
import { useFormik } from "formik";
import * as yup from "yup";

// React hook là những function cho ta sử dụng các tính năng của state, lifecycle =>PHẢI ĐƯỢC ĐẶT TRÊN CÙNG VÀ KO LỒNG TRONG BẤT CỨ HÀM NÀO KHÁC
const validationSchema = yup.object().shape({
  username: yup.string().required("This field is requied!"),
  email: yup.string().required("This field is requied").email("Invalid email!"),
  phone: yup
    .string()
    .required("This field is requied")
    .matches(/^[0-9]+$/g, "Invalid phone number!"),
});

const Home = () => {
  const [isGameStart, setIsGameStart] = useState(false);
  // const [a, setA] = useState();
  // const [b, setB] = useState();
  const formik = useFormik({
    initialValues: {
      username: "",
      email: "",
      phone: "",
    },
    validationSchema: validationSchema,
  });
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsGameStart(true);
    console.log(formik);
  };
  return (
    <>
      {isGameStart ? (
        <Game />
      ) : (
        <div
          className="text-center"
          style={{
            width: "100vw",
            height: "100vh",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <h1 className="diplay-4 mb-5"> Welcome to Pocker Center</h1>
          <h3>Fill your info and start</h3>
          <form onSubmit={handleSubmit} className="w-25 mx-auto">
            <input
              type="input"
              name="username"
              onChange={formik.handleChange}
              placeholder="username"
              className="w-100 form-control mb-3"
            />
            <input
              type="input"
              name="email"
              onChange={formik.handleChange}
              placeholder="email"
              className="w-100 form-control mb-3"
            />
            <input
              type="input"
              name="phone"
              onChange={formik.handleChange}
              placeholder="phone"
              className="w-100 form-control mb-3"
            />
            <button className="btn btn-success">Start new Game</button>
          </form>
        </div>
      )}
    </>
  );
};

export default Home;
