let initialState = {
  deckCard: {},
  isReveal: false,
  isDraw: true,
  isShuffle: false
};

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case "SET_DECK_CARD":
      state.deckCard = payload;
      return { ...state };
    case "REVEAL_CARDS":
      state.isReveal = true;
      state.isDraw = false;
      state.isShuffle = false;
      return { ...state };
    default:
      return state;
  }
};

export default reducer;
