import React, { Fragment, useEffect } from "react";
import "./index.css";
import Controls from "../../components/Control";
import Main from "../../components/Main";

const Game = () => {
  useEffect(() => { //useEffect đại diện cho cả 3 loại lifecycle như trong class: didMount, didUpdate, willUpdate
    console.log("game");
  })
  return (
    <Fragment> {/* Fragment chức năng giống thẻ <></> là thẻ rỗng, ko render trên giao diện */}
      <Controls />
      <Main />
    </Fragment>
  );
};

export default Game;
